tinymce.PluginManager.add('picture', function(editor, url) {
    editor.addButton('picture', {
        icon: 'picture',
        title: '插入文章',
        onclick: function() {
            editor.windowManager.open({
                title: '插入文章',
                width: 428,
                height: 248,
                body: [
                  {label: '文章地址', type: 'text', name:"url"}
                ]
            })
        }
    });
});
